{
  "project_name": "Solar-Powered Water Filtration System",
  "description": "A solar-powered water filtration unit capable of delivering potable water from raw sources in off-grid, environmentally sensitive, and low-maintenance contexts.",
  "objectives": [
    "Provide safe drinking water in off-grid locations.",
    "Require minimal user effort (≤ 10 minutes routine maintenance per day).",
    "Be affordable for target regions (≤ $500 household, ≤ $5,000 community).",
    "Use environmentally responsible materials and support end-of-life disposal.",
    "Be portable for households or easily palletized for community deployment."
  ],
  "functional_requirements": [
    {
      "id": 1,
      "description": "Deliver ≥ 10 L/h potable water (at 25°C, 1 atm) from sources with TDS ≤ 1000 mg/L."
    },
    {
      "id": 2,
      "description": "Achieve ≥ 4-log (99.99%) removal of bacteria, viruses, and 1 μm micro-plastics."
    },
    {
      "id": 3,
      "description": "Meet SR-01 and SR-02 under solar irradiance ≥ 300 W/m² (AM1.5)."
    },
    {
      "id": 4,
      "description": "Average electrical power consumption < 50 W at SR-01 flow-rate."
    },
    {
      "id": 5,
      "description": "Operate ≥ 6 hours without sunlight while maintaining SR-01 flow-rate."
    },
    {
      "id": 6,
      "description": "Operate from -10°C to 50°C and 0-95% RH with ≤ 10% performance loss."
    },
    {
      "id": 7,
      "description": "Have dry mass < 20 kg (household) and < 80 kg (community)."
    },
    {
      "id": 8,
      "description": "Use ≥ 60% recyclable product mass (ISO 14021) and exclude RoHS-restricted substances above thresholds."
    },
    {
      "id": 9,
      "description": "Allow untrained user to start/stop filtration in ≤ 3 actions and display water-quality status in < 2 seconds."
    },
    {
      "id": 10,
      "description": "Have delivered unit cost (FOB) ≤ $500 (household) and ≤ $5,000 (community) at 1,000 units/year."
    }
  ],
  "non_functional_requirements": [
    {
      "id": 1,
      "category": "Environmental",
      "description": "Must withstand dust and rain splash (minimum IP54 rating)."
    },
    {
      "id": 2,
      "category": "Power",
      "description": "100% solar-powered with integrated energy storage. External AC charger optional, not required for compliance."
    },
    {
      "id": 3,
      "category": "Interfaces",
      "description": "Water quality sensors must output digital readings via standard UART or I²C protocols."
    }
  ],
  "constraints": {
    "Budget": "≤ $500 (household), ≤ $5,000 (community)",
    "Materials": "Environmentally responsible materials with ≥ 60% recyclable product mass (ISO 14021)",
    "Legal": "Compliance with RoHS-restricted substances"
  },
  "assumptions": [
    "The system will be used in off-grid locations with varying environmental conditions.",
    "Users will have minimal technical expertise.",
    "Local regulations and standards will be complied with."
  ],
  "open_questions": []
}

